const shareButton = document.querySelector('.share-button');
const shareDialog = document.querySelector('.share-dialog');
const closeButton = document.querySelector('.close-button');

shareButton.addEventListener('click', event => {
  if (navigator.share) {
   navigator.share({
      title: 'FIND ME : '+"\n\n"+"Location Name : "+document.getElementById("location2").value+"\n\n"+"#Copyrights 2021 MVK"+"\n\n"+"Your Location Link :"+"\n",
      url: document.getElementById("result").value,
    }).then(() => {
      alert('ðŸ˜ŠðŸ˜Š Thanks for Sharing !! ðŸ˜ŠðŸ˜Š');
    })
    .catch(console.error);
    } else {
        shareDialog.classList.add('is-open');
    }
});

closeButton.addEventListener('click', event => {
  shareDialog.classList.remove('is-open');
});